const phrases=["I'm Dana Ismail","Marketing Executive"],el=document.querySelector("#typewiter");let sleepTime=100,curPhraseIndex=0;function sleep(e){return new Promise(s=>{setTimeout(s,e)})}const writeloop=async()=>{for(;;){let e=phrases[curPhraseIndex];for(let s=0;s<e.length;s++)el.innerHTML=e.substring(0,s+1),await sleep(sleepTime);await sleep(10*sleepTime);for(let s=e.length;s>0;s--)el.innerHTML=e.substring(0,s-1),await sleep(sleepTime);await sleep(5*sleepTime),curPhraseIndex===phrases.length-1?curPhraseIndex=0:curPhraseIndex++}};writeloop();